/*CSS FATTO*/

import React from "react";
import camera from "../assets/camera.svg";   
import {Form, Button} from "react-bootstrap"

import "../InserisciProp/InserimentoProprietà.css";



class ModificaBeB extends React.Component {

  constructor(props) {
    super(props);

    this.state = {

    }
  }

  onChange = (e) => {

  }

  onSubmit = (e) => {

  }

  render() {
    var bb = this.state.dati_bb;

    return (
        <div className="background">
      <div className="containerNew">  
        <div className="contentNew">
          <form>
          <h2>Modifica il tuo B&B con le informazioni che preferisci!</h2>
            <label htmlFor = "nome_proprieta">Nome</label>
              <input
                type = "text"
                id = "nome_proprieta"
                name = "nome_proprieta"
                defaultValue = {bb.nome_proprieta}
                onChange = {this.onChange}
                className = "i"
              />
              <label htmlFor = "localita">Città</label>
              <input
                type = "text"
                id = "localita"
                name = "localita"
                defaultValue = {bb.localita}
                onChange = {this.onChange}
                className = "i"
              />

              <label htmlFor = "indirizzo">Indirizzo</label>
              <input
                type = "text"
                id = "indirizzo"
                name = "indirizzo"
                defaultValue = {bb.indirizzo}
                onChange = {this.onChange}
                className = "i"
              />
              
              <label htmlFor = "provincia">Provincia</label>
              <input
                type = "text"
                id = "provincia"
                name = "provincia"
                defaultValue = {bb.provincia}
                onChange = {this.onChange}
                className = "i"
              />

              <label htmlFor = "servizi">Servizi</label>
              <input
                type = "text"
                id = "servizi"
                name = "servizi"
                defaultValue = {bb.servizi}
                onChange = {this.onChange}
                className = "i"
              />
              <label htmlFor = "descrizione">Descrizione</label>
              <textarea 
                id = 'descrizione'
                name = 'descrizione'
                defaultValue = {bb.descrizione}
                onChange = {this.onChange}
                className = 'iTA'
              >
              </textarea>

              <label htmlFor = "check_in">Check-in</label>
              <input
                type = "text"
                pattern = "^(0[0-9]|1[0-9]|2[0-3]).[0-5][0-9]$"
                title = "Inserire un orario corretto, usando un punto per separare i minuti"
                id = "check_in"
                name = "check_in"
                defaultValue = {bb.check_in}
                onChange = {this.onChange}
                className = "i"
              />

              <label htmlFor = "check_out">Check-out</label>
              <input
                type = "text"
                pattern = "^(0[0-9]|1[0-9]|2[0-3]).[0-5][0-9]$"
                title = "Inserire un orario corretto, usando un punto per separare i minuti"
                id = "check_out"
                name = "check_out"
                defaultValue = {bb.check_out}
                onChange = {this.onChange}
                className = "i"
              />
            
              <Button variant="primary" type="submit">
              Carica
            </Button>
          </form>
        </div>
      </div>
      </div>
    );
  }
}

export default ModificaBeB;
